const s = require('storyeng')
const should = require('should')

const inputs = {
  '  quit ': 'Quit',
  ' bye ': 'Quit',  
  '  good bye ': 'Quit',  
  ' good bye there now': 'Quit',
}

describe('actions', () => {

  it( 'prefab actions', done => {

    for (let str in inputs) {
      let e = new s.InputEvent(str)
      should(s.actionFor(e))
    }

    done()
  })

})